#'   target_col = "target",
#'   feature_cols = names(data)[1:10],
#'   min_bins = 3,              # Minimum 3 bins
#'   max_bins = 8,               # Maximum 8 bins
#'   sig_level = 0.10,           # 90% confidence level
#'   min_size = 0.03,            # Each bin at least 3% of data
#'   smooth = 1.0,               # Higher smoothing
