#'   cat_sep = " | ",            # Use pipe separator
#'   method = "chimerge_mod",    # Modified ChiMerge
#'   parallel = FALSE,           # No parallel processing
#'   cache = TRUE,               # Enable caching
#'   weights = NULL,             # No observation weights
#'   verbose = TRUE              # Show detailed logs
